    currenntWeather.error ?
    (<h2>{currenntWeather.error}</h2>) :
    ( <div>
        {
            currenntWeather.title
        }
    </div>)
}

const mapStateToProps = state => {
    return {
        currenntWeather : state.currenntWeather
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchCurrentWeather : () => dispatch(fetchCurrentWeather())
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
) (Weather)
