import React,{useState, useEffect} from 'react'
import { useSelector, useDispatch } from "react-redux"
import Axios from 'axios'
import {
    changeCity,
    CurrentWeatherData,
    CurrentWeatherError
} from './weatherSlice'
import styles from "./Weather.module.css";


function Weather() {
    const currentCity = useSelector(state => state.weather.currentCity);
    const currentData = useSelector(state => state.weather.currentData);
    const error = useSelector(state => state.weather.error);
    const dispatch = useDispatch()
    const API_KEY = "567fb009055f8d8fddf69948130c088f"
    const getCurrentWeather = (city) =>{
        Axios.get(`http://jsonplaceholder.typicode.com/posts/${city}`)
        // Axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${API_KEY}`)

        .then(response=>{
            // console.log(response.data)
            dispatch(CurrentWeatherData(response.data))
        })
        .catch(error=>{
            console.log(error.message)
            dispatch(CurrentWeatherError(error.message))
        })
    }

    useEffect(()=>{
        getCurrentWeather(currentCity)
    },[currentCity])

    const [newCity,setNewCity] = useState('Singapore')
    console.log(currentData)
    return (
        <div >
            <h1>Open Weather</h1>

            <input type="text"
            value={newCity}
            onChange={e=>setNewCity(e.target.value)}
            />

            <button 
            aria-label="Search By City"
            onClick= { () => dispatch(changeCity(newCity)) } >
            Search</button>

            {
                error && <h2>{error}</h2>
            }

            {
                currentData &&
                <div className={styles.weatherBox}>
                    <span className={styles.cityname}>{currentCity}</span>
                    <div className={styles.row}>
                        <div>
                            <img src="http://openweathermap.org/img/wn/02d.png" alt="Weather Icon" className={styles.icon}/>
                            <span className={styles.description}>{currentData.weather[0].description}</span>
                        </div>

                        <span className={styles.deg}>{currentData.wind.deg} °C</span>
                        <div className={styles.windBox}>
                            <span>Wind:{currentData.wind.speed} m/s</span> 
                            <span>Sunrise:{currentData.sys.sunrise}</span> 
                            <span>Sunset:{currentData.sys.sunset}</span> 
                        </div>
                    </div>
                    <div className={styles.row}>
                        <div>
                            <span >Test</span>
                            <img src="http://openweathermap.org/img/wn/02d.png" alt="Weather Icon" className={styles.icon}/>
                            <span >30 °C</span>
                        </div>
                    </div>
                </div>
            }
        </div>
    )
}

export default Weather
