import React,{useState, useEffect} from 'react'
import { useSelector, useDispatch } from "react-redux"
import Axios from 'axios'
import {
    changeCity,
    CurrentWeatherData,
    CurrentWeatherError
} from './weatherSlice'


function Weather() {
    const currentCity = useSelector(state => state.weather.currentCity);
    const currentData = useSelector(state => state.weather.currentData);
    const dispatch = useDispatch()
    const getCurrentWeather = (city) =>{
        Axios.get(`http://jsonplaceholder.typicode.com/posts/${city}`)
        .then(response=>{
            // console.log(response.data)
            dispatch(CurrentWeatherData(response.data))
        })
        .catch(error=>{
            console.log(error.message)
            dispatch(CurrentWeatherError(error.message))
        })
    }

    useEffect(()=>{
        getCurrentWeather(currentCity)
    },[currentCity])

    const [newCity,setNewCity] = useState(1)
    console.log(currentData)
    return (
        <div>
            <h1>Open Weather</h1>
            <input type="number"
            value={newCity}
            onChange={e=>setNewCity(e.target.value)}
            />
            <button onClick= { () => dispatch(changeCity(newCity)) } >
                Search</button>
            <h2>{currentCity}</h2>
            {
                currentData && <h2>{currentData.title}</h2>

            }
            {
                error && <h2>{error}</h2>
            }

        </div>
    )
}

export default Weather
