import React,{useState, useEffect} from 'react'
import { useSelector, useDispatch } from "react-redux"
import Axios from 'axios'
import {
    changeCity,
    CurrentWeatherData,
    CurrentWeatherError
} from './weatherSlice'
import styles from "./Weather.module.css";


function Weather() {
    const currentCity = useSelector(state => state.weather.currentCity);
    const currentData = useSelector(state => state.weather.currentData);
    const error = useSelector(state => state.weather.error);
    const dispatch = useDispatch()
    const API_KEY = "567fb009055f8d8fddf69948130c088f"
    const getCurrentWeather = (city) =>{
        Axios.get(`http://jsonplaceholder.typicode.com/posts/${city}`)
        // Axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${API_KEY}`)

        .then(response=>{
            // console.log(response.data)
            dispatch(CurrentWeatherData(response.data))
        })
        .catch(error=>{
            console.log(error.message)
            dispatch(CurrentWeatherError(error.message))
        })
    }

    useEffect(()=>{
        getCurrentWeather(currentCity)
    },[currentCity])

    const [newCity,setNewCity] = useState('Singapore')
    console.log(currentData)
    return (
        <div>
            <div className={styles.row}>

            </div>
            <h1>Open Weather</h1>
            <input type="text"
            value={newCity}
            onChange={e=>setNewCity(e.target.value)}
            />
            <button className={styles.button}
            aria-label="Search City"
            onClick= { () => dispatch(changeCity(newCity)) } >
                Search</button>
            <h2>{currentCity}</h2>
            <div className="row">
                <div className="col-md-4"></div>
            </div>
            {
                currentData && <h2>{currentData.title}</h2>
            }
            {
                error && <h2>{error}</h2>
            }

        </div>
    )
}

export default Weather
