import React,{useState,useEffect} from 'react'
import axios from 'axios'
import {
    FETCH_CURRENT_WEATHER_REQUEST,
    FETCH_CURRENT_WEATHER_SUCCESS,
    ETCH_CURRENT_WEATHER_FAILURE
} from './weatherActions'

function Weather() {
    const [city,setCity] = useState(1)
    const searchCity = (city) => {
        console.log(city)
    }
    const [currentWeather,setcurrentWeather] = useState({data:''})
    useEffect( ()=>{
        // axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=77a42762e10952649c361200298638e3`)
        axios.get(`http://jsonplaceholder.typicode.com/posts/${city}`)
        .then(response=>{
            // console.log(response.data)
            setcurrentWeather(response.data)
            console.log(currentWeather)
        })
        .catch(error=>{
            console.log(error)
        })
    },[] )
    return (
        <div>
            <h1>Open Weather</h1>
            <input type="text"
            value={city}
            onChange={e=>setCity(e.target.value)}
            />
            <button onClick={()=>searchCity(city)}>Search</button>
            <h2>{city}</h2>
            <h3></h3>
        </div>
    )
}

export default Weather
